function Lizard(startX, startY) {
	//this.size = 40 + 10 * (Math.random() * 2 - 1)
	this.size = 100 + 10 * (Math.random() * 2 - 1)
	this.legLength = this.size * 0.3;
	this.speed = 1;
	this.color = colorsToString(64*Math.random()|0, 64+64*Math.random()|0, 64*Math.random()|0);

	this.target = {x: startX, y: startY};

	this.body = Composite.create();
	
	// on the axis
	var torso = [];
	var torsoMusclesL = [];
	var torsoMusclesR = [];
	var positions = [-1 * this.size, -0.7 * this.size, -0.5 * this.size, 0, 0.5 * this.size, 1 * this.size, 1.5 * this.size];
	var sizes = [0.2 * this.size, 0.1 * this.size, 0.2 * this.size, 0.25 * this.size, 0.2 * this.size, 0.1 * this.size, 0.08 * this.size];
	var bodyPartParams = {frictionAir: 0.05, collisionFilter: {group: -1} };
	
	makeModules(this.body, torso, torsoMusclesL, torsoMusclesR, positions, sizes, bodyPartParams, 0.5, 0.5, 0.1 * this.size, 0.5, 0.1);
	
	var knees = [];
	var legs = [];
	
	makeLegs(this.body, torso, knees, legs, bodyPartParams, positions, this.legLength, 0.1 * this.size, 0.5);
	
	Composite.translate(this.body, {x: startX, y: startY});
	
	World.add(world, this.body);
	
	// target update ect.
	this.update = function () {
		// randomize muscles
		if (Math.random() < 0.01) {
			for (var i=0; i<torsoMusclesL.length; i++) {
				let delta = Math.sin(Math.random()*100)/10;
				torsoMusclesL[i].length = torsoMusclesL[i].length0 * (1+delta);
				torsoMusclesR[i].length = torsoMusclesR[i].length0 * (1-delta);
			}
		}
	}
	
	// starting objects, dx (local) and dy (local) for 3 points needed to draw a bezier curve
	this.curveNext = function (o1, x1, y1, o2, x2, y2, o3, x3, y3) {
		let p1 = Vector.add(o1.position, Vector.rotate({x: x1*this.size/100, y: y1*this.size/100}, o1.angle));
		let p2 = Vector.add(o2.position, Vector.rotate({x: x2*this.size/100, y: y2*this.size/100}, o2.angle));
		let p3 = Vector.add(o3.position, Vector.rotate({x: x3*this.size/100, y: y3*this.size/100}, o3.angle));
		ctx.bezierCurveTo(p1.x, p1.y, p2.x, p2.y, p3.x, p3.y);
	}
	
	
	// normal rendering
	this.draw = function () {
		ctx.fillStyle = 'black';//this.color;
		ctx.strokeStyle = 'black';
		ctx.beginPath();
		let p0 = Vector.add(torso[0].position, Vector.rotate({x: 0*this.size/100, y: -30*this.size/100}, torso[0].angle));
		ctx.moveTo(p0.x, p0.y);
		this.curveNext(torso[0], 10, -30, torso[0], 20, -10, torso[0], 20, 0); // head
		// this.curveNext(torso[0], 120, 0.25, torso[1], 30, 0.15, torso[1], 90, 0.1); // neck
		// this.curveNext(torso[1], 120, 0.1, torso[2], 60, 0.2, torso[2], 90, 0.2); // shoulder
		// this.curveNext(torso[2], 100, 0.25, knees[1], -5, 0.15, knees[1], 0, 0.1); // arm - top
		
		ctx.stroke();
		//ctx.fill();

	}
	
	// debug rendering
	this.debugDraw = function () {
		for (var b=0; b<Composite.allBodies(this.body).length; b++) {
			currentBody = Composite.allBodies(this.body)[b];			
			ctx.fillStyle = this.color;
			ctx.beginPath();
			ctx.arc(currentBody.position.x, currentBody.position.y, currentBody.circleRadius, 0, 2 * Math.PI);
			ctx.fill();
		}
		for (var c=0; c<Composite.allConstraints(this.body).length; c++) {
			currentConstraint = Composite.allConstraints(this.body)[c];
			ctx.strokeStyle = colorsToString(127+127*(currentConstraint.stiffness)|0,
				127+127*(currentConstraint.stiffness)|0,
				127+127*(currentConstraint.stiffness)|0);
			let ptA = Vector.add(currentConstraint.bodyA.position, currentConstraint.pointA);
			let ptB = Vector.add(currentConstraint.bodyB.position, currentConstraint.pointB);
			ctx.beginPath();
			ctx.moveTo(ptA.x, ptA.y);
			ctx.lineTo(ptB.x, ptB.y);
			ctx.stroke();
		}		
	}
}

function isPointOnDigit(x, y, unitSquare, digitPlace) {
	let digit = 0;
	let hours = currentTime.getHours();
	let minutes = currentTime.getMinutes();
	switch(digitPlace) {
		case "10h":
			if (hours<10) {
				return false;
			} else {
				digit = (hours < 20) ? 1 : 2;
			}
			break;
		case "1h":
			digit = parseInt(hours.toString().slice(-1), 10);
			break;
		case ":":
			digit = 10; // ":"
			break;
		case "10m":
			if (minutes<10) {
				digit = 0;
			} else {
				digit = parseInt(minutes.toString()[0], 10);
			}
			break;
		case "1m":
			digit = parseInt(minutes.toString().slice(-1), 10);
			break;
	}
	
	return (digits[Math.trunc(digit*30 + x*10/unitSquare)][Math.trunc(y*10/unitSquare)] > 3);
}

function isPointOnTime(x, y) {
	let x_cutoff = 0;
	let	y_cutoff = 0;
	let unitSquare = 0;
	if (canv.width*9/16 > canv.height) {
		x_cutoff = (canv.width - canv.height*16/9) / 2;
		unitSquare = canv.height / 9;
	} else {
		y_cutoff = (canv.height - canv.width*9/16) / 2;
		unitSquare = canv.width / 16;
	}
	
	if (x<x_cutoff || x>canv.width-x_cutoff || y<y_cutoff || y>canv.height-y_cutoff) {
		return false;
	}
	
	x -= x_cutoff;
	y -= y_cutoff;
	
	if (x<unitSquare || x>15*unitSquare || y<2*unitSquare || y>7*unitSquare) {
		return false;
	}
	
	x -= unitSquare;
	y -= 2*unitSquare;
	
	// the timer area is 14x5 unitSquare
	
	if (x<3*unitSquare) {
		return isPointOnDigit(x, y, unitSquare, "10h");
	} else if (x<6*unitSquare){
		return isPointOnDigit(x-3*unitSquare, y, unitSquare, "1h");
	} else if (x<8*unitSquare){
		return isPointOnDigit(x-6*unitSquare, y, unitSquare, ":");
	} else if (x<11*unitSquare){
		return isPointOnDigit(x-8*unitSquare, y, unitSquare, "10m");
	} else {
		return isPointOnDigit(x-11*unitSquare, y, unitSquare, "1m");
	}
}

function updateMouse(canv, evt) {
	var rect = canv.getBoundingClientRect();
	var new_x = evt.clientX - rect.left;
	var new_y = evt.clientY - rect.top;
	var new_speed = Math.sqrt(Math.pow(new_x-mousePosition.x, 2) + Math.pow(new_y-mousePosition.y, 2));
	var new_pred_x = new_x + 2 * (new_x - mousePosition.x);
	var new_pred_y = new_y + 2 * (new_y - mousePosition.y);
	mousePosition = {x:new_x, y:new_y, speed:new_speed, pred_x:new_pred_x, pred_y:new_pred_y};
}

