function renderFPS(dt) {
	ctx.fillStyle = "white";
	ctx.fillRect(0, 0, 40, 15);
	ctx.font = "12px Arial";
	ctx.fillStyle = "black";
	ctx.fillText((1/dt).toFixed() + " fps", 2, 12);
}

function animate() {
	var now = Date.now();
    var dt = (now - lastTime) / 1000.0;  // in seconds
	
	currentTime = new Date();
	
    update(dt);
    Engine.update(engine, dt);
	render();
	renderFPS(dt);
	
	lastTime = now;
	requestAnimationFrame(animate);
}

